mapper.update:
update CUP_TEAM set TEAM_NAME = #{team_name}, TEAM_LEADER = #{team_leader},
TEAM_UNIFORM_HOME = #{team_uniform_home}, TEAM_UNIFORM_AWAY = #{team_uniform_away}, TEAM_EMBLEM = #{team_emblem},
BRANCH_NO = #{branch_no} where TEAM_NO = #{team_no}

update cup_match_schedule set 
	SCHEDULE_HOME_GOAL = #{schedule_home_goal}, 
	SCHEDULE_AWAY_GOAL = #{schedule_away_goal} 
	WHERE SCHEDULE_NO = #{schedule_no}
